INSERT INTO `article` (`id`, `title`, `slug`, `meta`, `img`, `short_description`, `description`, `file_path`, `type`, `status`, `category_id`, `user_id_c`, `user_name_c`, `user_id_u`, `user_name_u`, `admin_id_c`, `admin_name_c`, `admin_id_u`, `admin_name_u`, `deleted_at`, `created_at`, `updated_at`) VALUES
(47, '4 lý do khiến nhà đầu tư săn lùng shophouse Europe trong tháng 8', '4-ly-do-khien-nha-dau-tu-san-lung-shophouse-europe-trong-thang-8', NULL, 'https://chkvietnam.vn/storage/upload/files/1566458663-ảnh 19.png', '<p><a href=\"https://news.zing.vn/bat-dong-san.html\" title=\"Bất động sản\">BẤT ĐỘNG SẢN</a></p>\r\n\r\n<h1><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">4 l&yacute; do khiến nh&agrave; đầu tư săn l&ugrave;ng shophouse Europe trong th&aacute;ng 8</span></span></h1>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chủ đầu tư hỗ trợ kinh doanh l&ecirc;n tới 1 tỷ đồng, c&ugrave;ng c&aacute;c ch&iacute;nh s&aacute;ch ưu đ&atilde;i t&agrave;i ch&iacute;nh hấp dẫn cho kh&aacute;ch mua nh&agrave; phố tại shophouse Europe, thuộc Sun Plaza Grand World trong th&aacute;ng 8.</span></span></p>', '<h1><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">4 l&yacute; do khiến nh&agrave; đầu tư săn l&ugrave;ng shophouse Europe trong th&aacute;ng 8</span></span></h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Chủ đầu tư hỗ trợ kinh doanh l&ecirc;n tới 1 tỷ đồng, c&ugrave;ng c&aacute;c ch&iacute;nh s&aacute;ch ưu đ&atilde;i t&agrave;i ch&iacute;nh hấp dẫn cho kh&aacute;ch mua nh&agrave; phố tại shophouse Europe, thuộc Sun Plaza Grand World trong th&aacute;ng 8.</span></span></p>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Với gần 150 căn shophouse giao dịch th&agrave;nh c&ocirc;ng chỉ trong th&aacute;ng 6 v&agrave; th&aacute;ng 7, ph&acirc;n khu nh&agrave; phố ch&acirc;u &Acirc;u thuộc tổ hợp Sun Plaza Grand World đang ghi nhận thanh khoản ấn tượng.</span></span></p>\r\n\r\n	<table align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"4 ly do khien nha dau tu san lung shophouse Europe trong thang 8 hinh anh 1 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2019_08_17/Anh_1.jpg\" style=\"height:939px; width:1600px\" /></span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ch&iacute;nh s&aacute;ch hỗ trợ c&ugrave;ng ưu điểm nội tại của shophouse Europe sẽ mở ra v&ocirc; v&agrave;n lợi &iacute;ch cho c&aacute;c chủ sở hữu tương lai.</span></span></td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tiếp nối sự s&ocirc;i động của thị trường Hạ Long, shophouse Europe tung ưu đ&atilde;i &ldquo;khủng&rdquo; d&agrave;nh cho nh&agrave; đầu tư trong th&aacute;ng 8.&nbsp;</span></span></p>\r\n\r\n	<h3><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hỗ trợ kinh doanh tới 1 tỷ</span></span></h3>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nhằm tạo điều kiện để chủ shophouse kinh doanh ng&agrave;nh dịch vụ ở t&acirc;m điểm du lịch B&atilde;i Ch&aacute;y, Hạ Long, shophouse Europe d&agrave;nh tặng g&oacute;i hỗ trợ kinh doanh l&ecirc;n tới&nbsp;1 tỷ đồng&nbsp;cho kh&aacute;ch h&agrave;ng giao dịch trong th&aacute;ng 8.</span></span></p>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Như vậy, nh&agrave; đầu tư mua shophouse th&aacute;ng n&agrave;y c&oacute; thể xem như vừa đầu tư đ&atilde; thu lợi nhuận. Nguồn t&agrave;i ch&iacute;nh n&agrave;y c&oacute; thể sử dụng để đầu tư cơ sở vật chất, mở nh&agrave; h&agrave;ng, kh&aacute;ch sạn v&agrave; kinh doanh c&aacute;c dịch vụ đa dạng kh&aacute;c khi nhận b&agrave;n giao shophouse (dự kiến) v&agrave;o cuối năm.</span></span></p>\r\n\r\n	<h3><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hỗ trợ vay 70% gi&aacute; trị sản phẩm</span></span></h3>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cũng nằm trong g&oacute;i ch&iacute;nh s&aacute;ch đang &aacute;p dụng tại dự &aacute;n<strong>,&nbsp;</strong>shophouse Europe hiện cho ph&eacute;p kh&aacute;ch h&agrave;ng được hỗ trợ vay tới 70% gi&aacute; trị sản phẩm, với l&atilde;i suất 0% trong 24 th&aacute;ng.</span></span></p>\r\n\r\n	<table align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"4 ly do khien nha dau tu san lung shophouse Europe trong thang 8 hinh anh 2 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2019_08_17/Anh_2__Shophouse_Europe_tung_uu_dai_khung_danh_cho_nha_dau_tu_trong_thang_8.jpg\" style=\"height:900px; width:1600px\" /></span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Shophouse Europe tung ưu đ&atilde;i &ldquo;khủng&rdquo; trong th&aacute;ng 8.</span></span></td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Như vậy, nh&agrave; đầu tư chỉ cần bỏ ra 30% gi&aacute; b&aacute;n đ&atilde; c&oacute; thể sở hữu kiến tr&uacute;c ch&acirc;u &Acirc;u nằm b&ecirc;n bờ vịnh Hạ Long. Trong khi đ&oacute;, ưu đ&atilde;i l&atilde;i suất 0% v&agrave; &acirc;n hạn nợ gốc trong 24 th&aacute;ng cho ph&eacute;p nh&agrave; đầu tư xuống tiền ở thời điểm hiện tại chỉ cần chờ 4-5 th&aacute;ng để bắt đầu nhận b&agrave;n giao shophouse v&agrave; c&oacute; khoảng 18 th&aacute;ng tiếp theo để kinh doanh kiếm lời m&agrave; kh&ocirc;ng chịu bất kỳ &aacute;p lực trả nợ, trả l&atilde;i n&agrave;o.</span></span></p>\r\n\r\n	<h3><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hưởng lợi từ hệ sinh th&aacute;i B&atilde;i Ch&aacute;y</span></span></h3>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tọa lạc b&ecirc;n bờ vịnh di sản, shophouse Europe gồm c&aacute;c d&atilde;y shophouse nằm tr&ecirc;n mặt đường Hạ Long, tiếp gi&aacute;p đại lộ &Uacute;c Phi v&agrave; kết nối thẳng tới tuyến đường bao biển B&atilde;i Ch&aacute;y. Đ&acirc;y cũng l&agrave; vị tr&iacute; t&acirc;m điểm của hệ sinh th&aacute;i du lịch B&atilde;i Ch&aacute;y.</span></span></p>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Từ những tổ hợp vui chơi, khu nghỉ dưỡng cho đến quảng trường, cung đường biển, hệ sinh th&aacute;i du lịch B&atilde;i Ch&aacute;y đ&atilde; kh&ocirc;ng ngừng ho&agrave;n thiện những năm qua. Sự xuất hiện của shophouse Europe l&agrave; giải ph&aacute;p ho&agrave;n hảo đ&aacute;p ứng nhu cầu mua sắm của lượng kh&aacute;ch khổng lồ tới với quần thể n&agrave;y mỗi năm.</span></span></p>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đặc biệt, nằm trong quần thể hệ sinh th&aacute;i du lịch, nghỉ dưỡng v&agrave; vui chơi giải tr&iacute; tỷ đ&ocirc; của Tập đo&agrave;n Sun Group tại B&atilde;i Ch&aacute;y, n&ecirc;n hoạt động kinh doanh của chủ sở hữu shophouse được đảm bảo d&ograve;ng kh&aacute;ch ổn định nhờ v&ocirc; số lễ hội, sự kiện được tổ chức suốt 4 m&ugrave;a tại đ&acirc;y. Từ đầu năm, B&atilde;i Ch&aacute;y đ&atilde; l&agrave; t&acirc;m điểm của c&aacute;c lễ hội như Sun Dance Festival, Sun Pool Party, Carnival Hạ Long&hellip;</span></span></p>\r\n\r\n	<table align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"4 ly do khien nha dau tu san lung shophouse Europe trong thang 8 hinh anh 3 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2019_08_17/Anh_3__To_hop_vui_choi_giai_tri_Sun_World_Halong_Complex.jpg\" style=\"height:1250px; width:1875px\" /></span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tổ hợp vui chơi giải tr&iacute; Sun World Halong Complex.</span></span></td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">M&ocirc; h&igrave;nh lễ hội, sự kiện đặc sắc đang được Hạ Long v&agrave; c&aacute;c nh&agrave; đầu tư chiến lược tại th&agrave;nh phố triển khai mạnh mẽ, với quy m&ocirc; ng&agrave;y c&agrave;ng tăng l&ecirc;n.</span></span></p>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mới đ&acirc;y, Hạ Long đ&atilde; được chọn lựa để đăng cai điểm đến cuộc đua thuyền buồm Clipper Race. Đơn vị tổ chức giải đấu cho biết, chặng đua sẽ dừng ch&acirc;n tại vịnh di sản Hạ Long trong 10 ng&agrave;y. Ngo&agrave;i ra, Clipper Race sẽ quảng b&aacute; c&aacute;c thương hiệu của Hạ Long, Quảng Ninh v&agrave; Việt Nam tr&ecirc;n c&aacute;c con t&agrave;u v&agrave; gh&eacute; qua hơn 200 nước tr&ecirc;n thế giới.</span></span></p>\r\n\r\n	<h3><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&oacute;n đầu du lịch</span></span></h3>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Sau c&uacute; lột x&aacute;c về hạ tầng của Quảng Ninh với h&agrave;ng loạt dự &aacute;n giao th&ocirc;ng về đ&iacute;ch trong năm 2018 v&agrave; 2019, c&acirc;u chuyện hạ tầng, quy hoạch tiếp tục được kỳ vọng l&agrave; c&uacute; h&iacute;ch cho BĐS Hạ Long. Trong đ&oacute;, đ&aacute;ng kể c&oacute; việc kh&aacute;nh th&agrave;nh đường bao biển B&atilde;i Ch&aacute;y, khởi c&ocirc;ng cao tốc V&acirc;n Đồn - M&oacute;ng C&aacute;i, v&agrave; việc quy hoạch mở rộng th&agrave;nh phố Hạ Long vừa ch&iacute;nh thức được ph&ecirc; duyệt v&agrave;o th&aacute;ng 6.</span></span></p>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hệ thống hạ tầng giao th&ocirc;ng hiện đại đi v&agrave;o vận h&agrave;nh (bao gồm s&acirc;n bay, cảng biển v&agrave; c&aacute;c tuyến cao tốc) cũng g&oacute;p phần mang về d&ograve;ng kh&aacute;ch du lịch lớn cho Quảng Ninh. 6 th&aacute;ng đầu năm, Quảng Ninh đ&oacute;n 8,5 triệu lượt kh&aacute;ch với 2,8 triệu lượt kh&aacute;ch quốc tế, bằng 1/3 lượng kh&aacute;ch ngoại cả nước nửa đầu 2019.</span></span></p>\r\n\r\n	<table align=\"center\">\r\n		<tbody>\r\n			<tr>\r\n				<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"4 ly do khien nha dau tu san lung shophouse Europe trong thang 8 hinh anh 4 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2019_08_17/Anh_4__Ha_Long_don_dong_khach_ngoai_cao_cap_tu_Cang_tau_khach_quoc_te_Ha_Long.JPG\" style=\"height:1080px; width:1920px\" /></span></span></td>\r\n			</tr>\r\n			<tr>\r\n				<td><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hạ Long đ&oacute;n d&ograve;ng kh&aacute;ch ngoại cao cấp từ cảng t&agrave;u kh&aacute;ch quốc tế Hạ Long.</span></span></td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n\r\n	<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Giới chuy&ecirc;n gia nhận định, dư địa ph&aacute;t triển ng&agrave;nh thương mại, dịch vụ ở Quảng Ninh c&ograve;n rất lớn khi tốc độ tăng trưởng du lịch của tỉnh n&agrave;y thuộc top đầu cả nước. Trong đ&oacute;, m&ocirc; h&igrave;nh c&aacute;c d&atilde;y nh&agrave; phố thương mại với những sản phẩm cao cấp như shophouse Europe được dự b&aacute;o tiếp tục dẫn đầu xu hướng v&agrave; h&uacute;t mạnh d&ograve;ng tiền từ giới đầu tư BĐS.</span></span></p>\r\n	</li>\r\n</ul>', NULL, 1, 1, 26, 0, NULL, 0, NULL, 1, 'admin', 1, 'admin', NULL, '2019-08-19 02:51:34', '2019-08-22 00:24:36'),
(48, 'Thiết kế nhà 3 tầng đẹp chỉ với 1 tỷ đồng', 'thiet-ke-nha-3-tang-dep-chi-voi-1-ty-dong', NULL, 'https://chkvietnam.vn/storage/upload/files/1566458275-ảnh 18.png', '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đối với những đại gia đ&igrave;nh, th&igrave; việc x&acirc;y nh&agrave; cao tầng để tối ưu ho&aacute; kh&ocirc;ng gian sinh hoạt l&agrave; điều cần thiết. Đặc biệt, với những mẫu nh&agrave; 3 tầng lu&ocirc;n được nhiều chủ nh&agrave; lựa chọn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tuy vậy, l&agrave;m thế n&agrave;o để thiết kế một mẫu nh&agrave; 3 tầng với gi&aacute; hợp l&yacute; nhất cũng l&agrave; c&acirc;u hỏi m&agrave; nhiều chủ nh&agrave; quan t&acirc;m.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Một trong những c&acirc;u hỏi về vấn đề x&acirc;y nh&agrave; 3 tầng được gửi về như sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>&ldquo;Gia đ&igrave;nh t&ocirc;i ở B&igrave;nh Thuận, hiện ng&acirc;n s&aacute;ch của ch&uacute;ng t&ocirc;i chỉ c&oacute; 1,5 tỷ đồng, muốn x&acirc;y nh&agrave; 3 tầng tr&ecirc;n mảnh đất 6x20m, nhờ kiến tr&uacute;c sư tư vấn gi&uacute;p.&rdquo;</em></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">KTS Phạm Thanh Truyền tư vấn thiết kế nh&agrave; 3 tầng đ&aacute;p ứng y&ecirc;u cầu như sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mảnh đất 6x20m của anh chị tương đối đẹp v&igrave; vậy m&igrave;nh n&ecirc;n để d&agrave;nh một phần diện t&iacute;ch trước, giữa v&agrave; sau nh&agrave; để l&agrave;m s&acirc;n vườn, gi&uacute;p m&agrave;u xanh len lỏi v&agrave;o từng kh&ocirc;ng gian sống. Tường bao quanh nh&agrave; n&ecirc;n x&acirc;y 20cm, c&oacute; c&aacute;ch lớp nhiệt để nh&agrave; được bảo vệ nh&agrave; tốt nhất, ph&ugrave; hợp với điều kiện tự nhi&ecirc;n của khu vực miền Trung.</span></span></p>', '<h1><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiết kế nh&agrave; 3 tầng đẹp chỉ với 1 tỷ đồng</span></span></h1>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Với 1 tỷ đồng, chủ nh&agrave; vẫn c&oacute; thể x&acirc;y dựng một ng&ocirc;i nh&agrave; 3 tầng đầy đủ tiện nghi cho đại gia đ&igrave;nh nếu biết c&aacute;ch thiết kế tối ưu kh&ocirc;ng gian.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"nhà 3 tầng\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-3.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Thiết kế nh&agrave; 3 tầng đẹp chỉ với 1 tỷ đồng</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đối với những đại gia đ&igrave;nh, th&igrave; việc x&acirc;y nh&agrave; cao tầng để tối ưu ho&aacute; kh&ocirc;ng gian sinh hoạt l&agrave; điều cần thiết. Đặc biệt, với những mẫu nh&agrave; 3 tầng lu&ocirc;n được nhiều chủ nh&agrave; lựa chọn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tuy vậy, l&agrave;m thế n&agrave;o để thiết kế một mẫu nh&agrave; 3 tầng với gi&aacute; hợp l&yacute; nhất cũng l&agrave; c&acirc;u hỏi m&agrave; nhiều chủ nh&agrave; quan t&acirc;m.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Một trong những c&acirc;u hỏi về vấn đề x&acirc;y nh&agrave; 3 tầng được gửi về như sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>&ldquo;Gia đ&igrave;nh t&ocirc;i ở B&igrave;nh Thuận, hiện ng&acirc;n s&aacute;ch của ch&uacute;ng t&ocirc;i chỉ c&oacute; 1,5 tỷ đồng, muốn x&acirc;y nh&agrave; 3 tầng tr&ecirc;n mảnh đất 6x20m, nhờ kiến tr&uacute;c sư tư vấn gi&uacute;p.&rdquo;</em></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">KTS Phạm Thanh Truyền tư vấn thiết kế nh&agrave; 3 tầng đ&aacute;p ứng y&ecirc;u cầu như sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mảnh đất 6x20m của anh chị tương đối đẹp v&igrave; vậy m&igrave;nh n&ecirc;n để d&agrave;nh một phần diện t&iacute;ch trước, giữa v&agrave; sau nh&agrave; để l&agrave;m s&acirc;n vườn, gi&uacute;p m&agrave;u xanh len lỏi v&agrave;o từng kh&ocirc;ng gian sống. Tường bao quanh nh&agrave; n&ecirc;n x&acirc;y 20cm, c&oacute; c&aacute;ch lớp nhiệt để nh&agrave; được bảo vệ nh&agrave; tốt nhất, ph&ugrave; hợp với điều kiện tự nhi&ecirc;n của khu vực miền Trung.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-1.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Với tổng diện t&iacute;ch mặt s&agrave;n x&acirc;y dựng 270m2 v&agrave; chi ph&iacute; tầm 1,5 tỷ đồng để x&acirc;y một ng&ocirc;i nh&agrave; 3 tầng, c&oacute; thể chia ra chi ph&iacute; x&acirc;y dựng cơ bản v&agrave;o khoảng 900 triệu đồng, chi ph&iacute; ho&agrave;n thiện to&agrave;n bộ nội thất gắn tường v&agrave; nội thất rời l&agrave; 600 triệu nữa. Đ&acirc;y l&agrave; mức gi&aacute; trung b&igrave;nh tr&ecirc;n thị trường hiện nay.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-2.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Để giảm gi&aacute; th&agrave;nh, vợ chồng anh chị n&ecirc;n sử dụng nhiều v&aacute;ch ngăn nhẹ để ngăn chia kh&ocirc;ng gian, nhằm hạn chế tải trọng c&ocirc;ng tr&igrave;nh. C&oacute; thể sử dụng một số v&aacute;ch ngăn bằng k&iacute;nh để tăng cảm gi&aacute;c rộng r&atilde;i cho kh&ocirc;ng gian.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-3.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đối với một căn nh&agrave; 3 tầng, quan trọng l&agrave; thiết kế c&aacute;c ph&ograve;ng li&ecirc;n th&ocirc;ng nhằm tăng tiện &iacute;ch đồng thời g&oacute;p phần giảm đ&aacute;ng kể chi ph&iacute;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-4.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nội thất n&ecirc;n sử dụng hệ thống tủ, kệ &acirc;m tường, liền tường gi&uacute;p kh&ocirc;ng gian gọn g&agrave;ng v&agrave; rộng r&atilde;i hơn, đặc biệt cũng l&agrave; một c&aacute;ch để tiết kiệm chi ph&iacute; cho nguy&ecirc;n vật liệu khi x&acirc;y dựng ng&ocirc;i nh&agrave; 3 tầng n&agrave;y.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-5.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&ograve;ng ngủ của con v&agrave; bố mẹ bố tr&iacute; tr&ecirc;n tầng 2. S&agrave;n c&aacute;c ph&ograve;ng ngủ l&aacute;t gỗ tạo cảm gi&aacute;c ấm c&uacute;ng. G&oacute;c l&agrave;m việc, học tập được ngăn ri&ecirc;ng với khu vực giường ngủ để đảm bảo sự ri&ecirc;ng tư cho mỗi kh&ocirc;ng gian</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-6.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mỗi ph&ograve;ng ngủ đều n&ecirc;n c&oacute; nh&agrave; vệ sinh kh&eacute;p k&iacute;n. Anh chị n&ecirc;n n&ecirc;n lựa chọn bồn tắm đứng để tiết kiệm chi ph&iacute;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-7.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ph&ograve;ng thờ, ph&ograve;ng giặt sẽ bố tr&iacute; tr&ecirc;n tầng 3. Diện t&iacute;ch ph&iacute;a trước bố tr&iacute; kh&ocirc;ng gian s&acirc;n vườn để tổ chức c&aacute;c bữa tiệc BBQ, đ&aacute;p ứng nhu cầu tiếp đ&oacute;n kh&aacute;ch, bạn b&egrave;&hellip;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-8.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&ocirc;ng gian ph&ograve;ng thờ cần sự trang nghi&ecirc;m n&ecirc;n được t&aacute;ch hẳn ra sau, kh&ocirc;ng bị lẫn v&agrave;o kh&ocirc;ng gian phụ trợ kh&aacute;c trong ng&ocirc;i nh&agrave; 3 tầng</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-9.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Bản vẽ mặt bằng tầng 1</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-10.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Bản vẽ mặt bằng tầng 2</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"thiet ke nha 3 tang dep chi voi 1 ty dong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/08/nha-3-tang-11.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Bản vẽ mặt bằng tầng 3</span></span></p>', NULL, 1, 1, 26, 0, NULL, 0, NULL, 1, 'admin', 1, 'admin', NULL, '2019-08-19 02:54:22', '2019-08-22 00:18:15'),
(49, 'Vùng ven Hà Nội: Bát nháo dự án đất nền “trên giấy\"', 'vung-ven-ha-noi-bat-nhao-du-an-dat-nen-“tren-giay', NULL, 'https://chkvietnam.vn/storage/upload/files/1566448539-ảnh 15.png', '<h2><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">B&aacute;n đất ph&acirc;n l&ocirc; khi dự &aacute;n chưa được ph&ecirc; duyệt</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Những ng&agrave;y cuối th&aacute;ng 7 vừa qua, tại c&aacute;c huyện, thị x&atilde;: Thạch Thất, Quốc Oai, Sơn T&acirc;y c&oacute; nhiều dự &aacute;n được quảng c&aacute;o rầm rộ. Tại nhiều trang chuy&ecirc;n đăng tải th&ocirc;ng tin mua - b&aacute;n bất động sản... c&aacute;c dự &aacute;n n&oacute;i tr&ecirc;n đều được quảng c&aacute;o hấp dẫn như: Hạ tầng đồng bộ; gần &ldquo;si&ecirc;u đ&ocirc; thị vệ tinh H&ograve;a Lạc&rdquo;; được cấp giấy chứng nhận quyền sử dụng đất ở vĩnh viễn&hellip; Điểm chung của c&aacute;c dự &aacute;n n&agrave;y theo giới thiệu l&agrave; đ&atilde; được ph&acirc;n l&ocirc;, diện t&iacute;ch 70-150m2/l&ocirc;, gi&aacute; 9,5-18,5 triệu đồng/m2 (t&ugrave;y từng dự &aacute;n).</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo t&igrave;m hiểu, khu đất được quảng c&aacute;o l&agrave; nơi triển khai một dự &aacute;n nằm ở khu suối Ốc, th&ocirc;n Đồng Trạng, x&atilde; Cổ Đ&ocirc;ng (thị x&atilde; Sơn T&acirc;y) hiện kh&ocirc;ng c&oacute; bất cứ dấu hiệu g&igrave; li&ecirc;n quan đến việc dự &aacute;n đang triển khai x&acirc;y dựng. Ngạc nhi&ecirc;n hơn, khu đất n&agrave;y rộng 38.487,3m2, đ&atilde; được cấp giấy chứng nhận quyền sử dụng đất cho hộ &ocirc;ng Nguyễn Thanh Hải, x&atilde; Cổ Đ&ocirc;ng để sử dụng trồng c&acirc;y l&acirc;u năm v&agrave; nu&ocirc;i trồng thủy sản.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tương tự, tại th&ocirc;n 6, x&atilde; Ph&uacute; C&aacute;t (huyện Quốc Oai) cũng c&oacute; dự &aacute;n đang rao b&aacute;n rầm rộ tr&ecirc;n c&aacute;c trang mạng. Trong khu&ocirc;n vi&ecirc;n dự &aacute;n n&agrave;y trồng một số c&acirc;y ngắn ng&agrave;y như sắn, riềng... v&agrave; cũng kh&ocirc;ng c&oacute; bất cứ dấu hiệu g&igrave; của việc dự &aacute;n đang triển khai x&acirc;y dựng. Trong khi đ&oacute;, khu đất c&oacute; diện t&iacute;ch hơn 4.500m2 (trong đ&oacute; c&oacute; 400m2 đất ở) đứng t&ecirc;n chủ sử dụng l&agrave; b&agrave; Nguyễn Tường Vy (quận T&acirc;y Hồ), mục đ&iacute;ch sử dụng để ở v&agrave; trồng c&acirc;y. Hiện, thửa đất n&agrave;y đ&atilde; được chuyển nhượng cho &ocirc;ng Nguyễn Xu&acirc;n Nam v&agrave; b&agrave; Nguyễn Thị Thu Hương.</span></span></p>', '<h2><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">B&aacute;n đất ph&acirc;n l&ocirc; khi dự &aacute;n chưa được ph&ecirc; duyệt</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Những ng&agrave;y cuối th&aacute;ng 7 vừa qua, tại c&aacute;c huyện, thị x&atilde;: Thạch Thất, Quốc Oai, Sơn T&acirc;y c&oacute; nhiều dự &aacute;n được quảng c&aacute;o rầm rộ. Tại nhiều trang chuy&ecirc;n đăng tải th&ocirc;ng tin mua - b&aacute;n bất động sản... c&aacute;c dự &aacute;n n&oacute;i tr&ecirc;n đều được quảng c&aacute;o hấp dẫn như: Hạ tầng đồng bộ; gần &ldquo;si&ecirc;u đ&ocirc; thị vệ tinh H&ograve;a Lạc&rdquo;; được cấp giấy chứng nhận quyền sử dụng đất ở vĩnh viễn&hellip; Điểm chung của c&aacute;c dự &aacute;n n&agrave;y theo giới thiệu l&agrave; đ&atilde; được ph&acirc;n l&ocirc;, diện t&iacute;ch 70-150m2/l&ocirc;, gi&aacute; 9,5-18,5 triệu đồng/m2 (t&ugrave;y từng dự &aacute;n).</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Theo t&igrave;m hiểu, khu đất được quảng c&aacute;o l&agrave; nơi triển khai một dự &aacute;n nằm ở khu suối Ốc, th&ocirc;n Đồng Trạng, x&atilde; Cổ Đ&ocirc;ng (thị x&atilde; Sơn T&acirc;y) hiện kh&ocirc;ng c&oacute; bất cứ dấu hiệu g&igrave; li&ecirc;n quan đến việc dự &aacute;n đang triển khai x&acirc;y dựng. Ngạc nhi&ecirc;n hơn, khu đất n&agrave;y rộng 38.487,3m2, đ&atilde; được cấp giấy chứng nhận quyền sử dụng đất cho hộ &ocirc;ng Nguyễn Thanh Hải, x&atilde; Cổ Đ&ocirc;ng để sử dụng trồng c&acirc;y l&acirc;u năm v&agrave; nu&ocirc;i trồng thủy sản.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tương tự, tại th&ocirc;n 6, x&atilde; Ph&uacute; C&aacute;t (huyện Quốc Oai) cũng c&oacute; dự &aacute;n đang rao b&aacute;n rầm rộ tr&ecirc;n c&aacute;c trang mạng. Trong khu&ocirc;n vi&ecirc;n dự &aacute;n n&agrave;y trồng một số c&acirc;y ngắn ng&agrave;y như sắn, riềng... v&agrave; cũng kh&ocirc;ng c&oacute; bất cứ dấu hiệu g&igrave; của việc dự &aacute;n đang triển khai x&acirc;y dựng. Trong khi đ&oacute;, khu đất c&oacute; diện t&iacute;ch hơn 4.500m2 (trong đ&oacute; c&oacute; 400m2 đất ở) đứng t&ecirc;n chủ sử dụng l&agrave; b&agrave; Nguyễn Tường Vy (quận T&acirc;y Hồ), mục đ&iacute;ch sử dụng để ở v&agrave; trồng c&acirc;y. Hiện, thửa đất n&agrave;y đ&atilde; được chuyển nhượng cho &ocirc;ng Nguyễn Xu&acirc;n Nam v&agrave; b&agrave; Nguyễn Thị Thu Hương.</span></span></p>', NULL, 1, 1, 26, 0, NULL, 0, NULL, 1, 'admin', 1, 'admin', NULL, '2019-08-19 02:57:06', '2019-08-21 21:35:53'),
(50, 'Cách bố trí và chăm sóc cây phát tài trong văn phòng', 'cach-bo-tri-va-cham-soc-cay-phat-tai-trong-van-phong', NULL, 'https://chkvietnam.vn/storage/upload/files/1566448320-ảnh14.png', '<h1><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;ch bố tr&iacute; v&agrave; chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i trong văn ph&ograve;ng</span></span></h1>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nếu biết c&aacute;ch bố tr&iacute; v&agrave; chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i ở văn ph&ograve;ng, kh&ocirc;ng chỉ sẽ gi&uacute;p g&oacute;c l&agrave;m việc của bạn &ldquo;xanh&rdquo; hơn, m&agrave; c&ograve;n tăng vận may t&agrave;i lộc.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"chăm sóc cây phát tài\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/06/cham-soc-cay-phat-tai-2.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;ch bố tr&iacute; v&agrave; chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i trong văn ph&ograve;ng</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lựa chọn c&acirc;y ph&aacute;t t&agrave;i nhiều may mắn</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&acirc;y Ph&aacute;t t&agrave;i c&oacute; t&ecirc;n tiếng Anh l&agrave; lucky bamboo, c&oacute; cấu tr&uacute;c nhiều đốt rỗng như th&acirc;n tre tr&uacute;c n&ecirc;n theo phong thủy sẽ gi&uacute;p cho mọi người xung quanh đả th&ocirc;ng suy nghĩ, t&acirc;m hồn được tự do ph&aacute;t triển kh&ocirc;ng bị b&oacute; buộc g&ograve; &eacute;p. B&ecirc;n cạnh đ&oacute;, vẻ ngo&agrave;i tr&agrave;n đầy sức sống sẽ mang lại t&agrave;i lộc v&agrave; may mắn cho chủ nh&acirc;n của n&oacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Để chọn được c&acirc;y ph&aacute;t t&agrave;i ưng &yacute;, khi mua c&acirc;y, nhớ chọn những c&acirc;y c&oacute; vẻ ngo&agrave;i tr&agrave;n đầy sức sống, l&aacute; xanh mướt, th&acirc;n c&acirc;y thẳng đứng cứng c&aacute;p, tr&aacute;nh chọn những c&acirc;y c&oacute; nhiều l&aacute; v&agrave;ng, l&agrave; &uacute;a, hoặc l&aacute; kh&ocirc;ng x&ograve;e rộng, th&acirc;n c&acirc;y c&ograve;i cọc&hellip; B&ecirc;n cạnh đ&oacute;, trước khi t&igrave;m hiểu về c&aacute;ch chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i, h&atilde;y nhớ chọn lựa số lượng c&acirc;y trong b&igrave;nh/chậu ph&ugrave; hợp với từng &yacute; nghĩa của n&oacute;:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu t&igrave;nh y&ecirc;u v&agrave; h&ocirc;n nh&acirc;n: loại c&oacute; 2 c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu hạnh ph&uacute;c: loại c&oacute; 3 c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu sức khỏe: loại c&oacute; 5 c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu thịnh vượng: loại c&oacute; 8 c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu may mắn: loại c&oacute; 9 c&acirc;y.</span></span></p>', '<h1><span style=\"font-size:20px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;ch bố tr&iacute; v&agrave; chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i trong văn ph&ograve;ng</span></span></h1>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Nếu biết c&aacute;ch bố tr&iacute; v&agrave; chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i ở văn ph&ograve;ng, kh&ocirc;ng chỉ sẽ gi&uacute;p g&oacute;c l&agrave;m việc của bạn &ldquo;xanh&rdquo; hơn, m&agrave; c&ograve;n tăng vận may t&agrave;i lộc.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"chăm sóc cây phát tài\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/06/cham-soc-cay-phat-tai-2.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;ch bố tr&iacute; v&agrave; chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i trong văn ph&ograve;ng</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lựa chọn c&acirc;y ph&aacute;t t&agrave;i nhiều may mắn</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&acirc;y Ph&aacute;t t&agrave;i c&oacute; t&ecirc;n tiếng Anh l&agrave; lucky bamboo, c&oacute; cấu tr&uacute;c nhiều đốt rỗng như th&acirc;n tre tr&uacute;c n&ecirc;n theo phong thủy sẽ gi&uacute;p cho mọi người xung quanh đả th&ocirc;ng suy nghĩ, t&acirc;m hồn được tự do ph&aacute;t triển kh&ocirc;ng bị b&oacute; buộc g&ograve; &eacute;p. B&ecirc;n cạnh đ&oacute;, vẻ ngo&agrave;i tr&agrave;n đầy sức sống sẽ mang lại t&agrave;i lộc v&agrave; may mắn cho chủ nh&acirc;n của n&oacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Để chọn được c&acirc;y ph&aacute;t t&agrave;i ưng &yacute;, khi mua c&acirc;y, nhớ chọn những c&acirc;y c&oacute; vẻ ngo&agrave;i tr&agrave;n đầy sức sống, l&aacute; xanh mướt, th&acirc;n c&acirc;y thẳng đứng cứng c&aacute;p, tr&aacute;nh chọn những c&acirc;y c&oacute; nhiều l&aacute; v&agrave;ng, l&agrave; &uacute;a, hoặc l&aacute; kh&ocirc;ng x&ograve;e rộng, th&acirc;n c&acirc;y c&ograve;i cọc&hellip; B&ecirc;n cạnh đ&oacute;, trước khi t&igrave;m hiểu về c&aacute;ch chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i, h&atilde;y nhớ chọn lựa số lượng c&acirc;y trong b&igrave;nh/chậu ph&ugrave; hợp với từng &yacute; nghĩa của n&oacute;:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu t&igrave;nh y&ecirc;u v&agrave; h&ocirc;n nh&acirc;n: loại c&oacute; 2 c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu hạnh ph&uacute;c: loại c&oacute; 3 c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu sức khỏe: loại c&oacute; 5 c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu thịnh vượng: loại c&oacute; 8 c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn c&acirc;y ph&aacute;t t&agrave;i cầu may mắn: loại c&oacute; 9 c&acirc;y.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"cach bo tri va cham soc cay phat tai trong van phong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/06/cham-soc-cay-phat-tai-1.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>C&acirc;y ph&aacute;t t&agrave;i mang lại nhiều may mắn</em></span></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;ch bố tr&iacute; v&agrave; chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i ở văn ph&ograve;ng</span></span></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">C&acirc;y ph&aacute;t t&agrave;i được xem l&agrave; loại c&acirc;y mang tới t&agrave;i lộc may mắn, rất c&oacute; lợi cho con đường c&ocirc;ng danh n&ecirc;n ng&agrave;y nay c&oacute; kh&aacute; nhiều người mua để trang tr&iacute; văn ph&ograve;ng. Khi bố tr&iacute; c&acirc;y cần ch&uacute; &yacute; C&acirc;y Ph&aacute;t T&agrave;i thuộc h&agrave;nh Thủy, theo ngũ h&agrave;nh tương sinh th&igrave; Thủy sinh Mộc n&ecirc;n c&acirc;y kh&aacute; ph&ugrave; hợp với người mệnh Thủy hoặc Mộc. B&ecirc;n cạnh đ&oacute;, hướng Đ&ocirc;ng Nam thuộc h&agrave;nh Mộc v&agrave; hướng Bắc thuộc h&agrave;nh thủy l&agrave; những nơi th&iacute;ch hợp nhất để đặt c&acirc;y cảnh tr&ecirc;n b&agrave;n l&agrave;m việc.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&ocirc;ng n&ecirc;n đặt c&acirc;y ở hướng T&acirc;y v&agrave; T&acirc;y Bắc thuộc h&agrave;nh Kim, nếu đặt hướng n&agrave;y c&oacute; thể kh&ocirc;ng mang đến may mắn t&agrave;i lộc m&agrave; c&ograve;n bị h&uacute;t hết vận may.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ngo&agrave;i việc lưu t&acirc;m về c&aacute;ch bố tr&iacute; c&acirc;y ph&aacute;t t&agrave;i ở văn ph&ograve;ng dựa v&agrave;o yếu tố phong thủy, bạn cần phải lưu &yacute; th&ecirc;m vấn đề chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i:</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Nơi đặt c&acirc;y phải tho&aacute;ng m&aacute;t v&agrave; c&oacute; nhiều &aacute;nh s&aacute;ng tự nhi&ecirc;n nhất c&oacute; thể. Nhưng kh&ocirc;ng phải b&agrave;n l&agrave;m việc n&agrave;o cũng đảm bảo được điều n&agrave;y, nếu kh&ocirc;ng c&oacute; &aacute;nh nắng chiếu v&agrave;o th&igrave; đừng qu&ecirc;n khi chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i, h&atilde;y d&agrave;nh một khoảng thời gian nhất định để mang c&acirc;y ra nắng một l&uacute;c để c&acirc;y ph&aacute;t triển khỏe mạnh.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"cach bo tri va cham soc cay phat tai trong van phong\" src=\"http://www.muabannhadat.vn/tin-tuc/wp-content/uploads/2019/06/cham-soc-cay-phat-tai.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>N&ecirc;n quan t&acirc;m chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i để ph&aacute;t huy lợi &iacute;ch phong thuỷ tốt nhất</em></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- C&acirc;y kh&ocirc;ng n&ecirc;n để gần m&aacute;y sưởi, m&aacute;y lạnh, l&agrave;m ảnh hưởng đến khả năng ph&aacute;t triển của c&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Một mẹo nhỏ khi chăm s&oacute;c c&acirc;y ph&aacute;t t&agrave;i nữa l&agrave; c&acirc;y ưa nước, nhưng ch&uacute;ng kh&ocirc;ng th&iacute;ch giữ nước bởi vậy bạn cần chọn loại đất trồng ph&ugrave; hợp với đặc t&iacute;nh của c&acirc;y. H&atilde;y chọn loại đất dễ tho&aacute;t nước để kh&ocirc;ng bị l&agrave;m &uacute;ng c&acirc;y, pha đất theo c&ocirc;ng thức 80% trấu đen (loại tro trấu) + 20% trấu sống.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- Chọn chậu c&acirc;y: N&ecirc;n c&acirc;n đối k&iacute;ch thước đường k&iacute;nh của c&acirc;y v&agrave; chiều cao, nếu chậu qu&aacute; lớn hay qu&aacute; nhỏ đều ảnh hưởng tới sự sinh trưởng của c&acirc;y. Nếu lỗ ở chậu nhỏ h&atilde;y đục th&ecirc;m để đảm bảo cho việc tho&aacute;t nước dư thừa một c&aacute;ch dễ d&agrave;ng, tr&aacute;nh trường hợp đọng nước trong chậu, hợp chất hữu cơ trong chậu sẽ h&uacute;t nước từ đ&aacute;y chậu l&agrave;m cho rễ nở ra v&agrave; dễ bị &uacute;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">- C&acirc;y trồng ở văn ph&ograve;ng n&ecirc;n giảm lượng nước tưới, nếu để c&acirc;y trong ph&ograve;ng c&oacute; quạt th&igrave; tăng lượng nước tưới l&ecirc;n khoảng 30% so với ph&ograve;ng lạn</span></span>h.</p>', NULL, 1, 1, 26, 0, NULL, 0, NULL, 1, 'admin', 1, 'admin', NULL, '2019-08-19 03:00:45', '2019-08-21 21:32:18');
